const user = {
  firstName: 'Harper',
  lastName: 'Perez'
};
function getGreeting(user) {
  if (user) {
    return user.firstName;
  }
  return <h4>Hello, Stranger.</h4>;
};

const element =(
  <h2>
   {getGreeting("aa")}
  </h2>,
    <h2>
    {getGreeting(user)}
    </h2>
);

function Element2() {
    return (
      <div className="App">
        {element}
        <input type="text"/>
      </div>
    );
  }
  
  export default Element2;
